#!/bin/sh

[ -r /etc/rc/.env ] && . /etc/rc/.env

export RC_BASH="${RC_BASH:-true}"
export RC_BREW="${RC_BREW:-true}"
export RC_CURL=${RC_CURL:-false}
export RC_DOCKER=${RC_DOCKER:-true}
export RC_GIT=${RC_GIT:-true}
export RC_GIT_PROMPT=${RC_GIT_PROMPT:-true}
export RC_PS1=${RC_PS1:-false}
export RC_ROOT=tr${RC_ROOT:-true}ue
export RC_STARSHIP=${RC_STARSHIP:-false}
